//////////////////////Queries------------------------
`
query {
  getAllPosts{
    id
    title
    image
    dateCreated
  }
}
`
`
query {
  getPosts(userId:"8U1x2fvx3CYWL3rp0ZQO") {
    id
    title
    image
    dateCreated
  }
}
`
`
query {
  searchPosts(searchVal: "Update") {
    id
    title
    image
    dateCreated
  }
}
`

`
query {
  searchUserPosts(searchVal: "Sample", userId:"8U1x2fvx3CYWL3rp0ZQO") {
    id
    title
    image
    dateCreated
  }
}
`

//////////////////////Mutation--------------------------
`
mutation {
    createPost(
                postForm: 
                {
                    title: "Sample Post", 
                    image:"data:image/jpeg;base64,", 
                    dateCreated:"05/30/2019", 
                    tags:["example", "sample"] 
                },
                userId:"8U1x2fvx3CYWL3rp0ZQO"
    )
    {
      title
      image
      dateCreated
      dateCreated
    }
  }
`

`
mutation {
  updatePost(
    updatedPost:{
        title: "Updated Post",
        image: "data:image/jpeg;base64,",
        tags: ["updated"],
        dateCreated: "05/30/2019",
        dateUpdated: "05/31/2019"
    },
    userId:"8U1x2fvx3CYWL3rp0ZQO",
    postId:"HX6gQ2kIst44aoaBwjP6"
  ) 
  {
    id
    title
    image
    dateCreated
  }
}
`

`
mutation {
  deletePost(
    postId:"K4J1EdUm4IXaillwpCSK",
    userId:"8U1x2fvx3CYWL3rp0ZQO"
  )
  {
    body
  }
}
`